import{e as t,o as a,G as o}from"./vendor.bcce97f3.js";const r={class:"markdown-body"},e=[o('<h1>Avatar 头像</h1><h3>介绍</h3><p>用来代表用户或事物，支持图片、图标或字符展示。</p><h3>安装</h3><pre><code class="language-javascript">import { createApp } from &#39;vue&#39;;\n// vue\nimport { Avatar } from &#39;@nutui/nutui&#39;;\n// taro\nimport { Avatar } from &#39;@nutui/nutui-taro&#39;;\n\nconst app = createApp();\napp.use(Avatar);\n</code></pre><h2>代码示例</h2><h3>基本用法</h3><p>内置 smal / normal / large 三种尺寸规格</p><pre><code class="language-html">&lt;nut-avatar size=&quot;large&quot; icon=&quot;https://img12.360buyimg.com/imagetools/jfs/t1/143702/31/16654/116794/5fc6f541Edebf8a57/4138097748889987.png&quot;\n&gt;&lt;/nut-avatar&gt;\n&lt;nut-avatar size=&quot;normal&quot; icon=&quot;https://img12.360buyimg.com/imagetools/jfs/t1/143702/31/16654/116794/5fc6f541Edebf8a57/4138097748889987.png&quot;\n&gt;&lt;/nut-avatar&gt;\n&lt;nut-avatar size=&quot;small&quot; icon=&quot;https://img12.360buyimg.com/imagetools/jfs/t1/143702/31/16654/116794/5fc6f541Edebf8a57/4138097748889987.png&quot;\n&gt;&lt;/nut-avatar&gt;  \n</code></pre><h3>修改形状类型</h3><pre><code class="language-html">&lt;nut-avatar icon=&quot;my&quot; shape=&quot;square&quot;&gt;&lt;/nut-avatar&gt;\n&lt;nut-avatar icon=&quot;my&quot; shape=&quot;round&quot;&gt;&lt;/nut-avatar&gt;\n</code></pre><h3>修改背景色</h3><pre><code class="language-html">&lt;nut-avatar icon=&quot;my&quot; bg-color=&quot;#FA2C19&quot; /&gt;\n</code></pre><h3>修改背景icon</h3><pre><code class="language-html">&lt;nut-avatar icon=&quot;https://img12.360buyimg.com/imagetools/jfs/t1/196430/38/8105/14329/60c806a4Ed506298a/e6de9fb7b8490f38.png&quot; /&gt;\n</code></pre><h3>设置头像的文本内容</h3><pre><code class="language-html">&lt;nut-avatar&gt;N&lt;/nut-avatar&gt;\n</code></pre><h3>Prop</h3><table><thead><tr><th>字段</th><th>说明</th><th>类型</th><th>默认值</th></tr></thead><tbody><tr><td>bg-color</td><td>设置头像背景色</td><td>String</td><td>#eee</td></tr><tr><td>size</td><td>设置头像的大小，提供三种：large/normal/small，支持直接输入数字</td><td>String</td><td>normal</td></tr><tr><td>shape</td><td>设置头像的形状，默认是圆形，可以设置为square方形</td><td>String</td><td>round</td></tr><tr><td>icon</td><td>设置头像的icon图标, 类似Icon组件的name属性，支持名称和链接</td><td>String</td><td>‘’</td></tr></tbody></table><h3>Events</h3><table><thead><tr><th>字段</th><th>说明</th><th>类型</th><th>回调参数</th></tr></thead><tbody><tr><td>active-avatarror</td><td>点击触发事件</td><td>Function</td><td>event</td></tr></tbody></table>',21)],n={setup:o=>(o,n)=>(a(),t("div",r,e))};export{n as default};
