import{e as t,o as d,G as e}from"./vendor.bcce97f3.js";const a={class:"markdown-body"},r=[e('<h1>Barrage 组件</h1><h3>介绍</h3><p>用于话语和词组的轮播展示，适用于视频中或其他类似需求中。</p><h3>安装</h3><pre><code class="language-javascript">import { createApp } from &#39;vue&#39;;\nimport { Barrage } from &#39;@nutui/nutui&#39;;\n\nconst app = createApp();\napp.use(Barrage);\n\n</code></pre><h2>代码演示</h2><h3>基础用法1</h3><p><code>Icon</code> 的 <code>name</code> 属性支持传入图标名称或图片链接。</p><pre><code class="language-html">&lt;nut-barrage ref=&quot;danmu&quot; :danmu=&quot;list&quot;&gt;&lt;/nut-barrage&gt;\n</code></pre><pre><code class="language-javascript">  setup() {\n    const inputVal = ref&lt;any&gt;(&#39;&#39;);\n    const danmu = ref&lt;any&gt;(null);\n    let list = ref([&quot;画美不看&quot;, &quot;不明觉厉&quot;, &quot;喜大普奔&quot;, &quot;男默女泪&quot;, &quot;累觉不爱&quot;, &quot;爷青结&quot;]); \n    function addDanmu() {\n      danmu.value.add(inputVal.value);\n    }\n    return {\n      inputVal,\n      danmu,\n      list,\n      addDanmu\n    };\n  }\n</code></pre><h2>API</h2><h3>Props</h3><table><thead><tr><th>参数</th><th>说明</th><th>类型</th><th>默认值</th></tr></thead><tbody><tr><td>danmu</td><td>弹幕列表数据</td><td>Array</td><td>[]</td></tr><tr><td>frequency</td><td>可视区域内每个弹幕出现的时间间隔</td><td>Number</td><td>200</td></tr><tr><td>speeds</td><td>每个弹幕的滚动时间</td><td>Number</td><td>2000</td></tr><tr><td>rows</td><td>弹幕行数，分几行展示</td><td>Number</td><td>1</td></tr><tr><td>top</td><td>弹幕垂直距离</td><td>Number</td><td>10</td></tr><tr><td>loop</td><td>是否循环播放</td><td>Boolean</td><td>true</td></tr></tbody></table><h3>Events</h3><table><thead><tr><th>事件名</th><th>说明</th><th>回调参数</th></tr></thead><tbody><tr><td>add</td><td>添加数据</td><td>-</td></tr></tbody></table>',15)],n={setup:e=>(e,n)=>(d(),t("div",a,r))};export{n as default};
